import './App.css';
import {useEffect, useState} from "react";

function Dreieck() {

    const [alpha, setalpha] = useState(0);
    const [beta, setbeta] = useState(0);
    const [gamma, setgamma] = useState(0);

    function calculategamma(){
      let result;
      result = 180-alpha-beta;
        setgamma(result);
    }

    function draw() {
      const canvas = document.querySelector('#canvas');

      if (!canvas.getContext) {
          return;
      }
      const ctx = canvas.getContext('2d');

      // set line stroke and line width
      ctx.strokeStyle = 'red';
      ctx.lineWidth = 5;

      // draw a red line
      ctx.beginPath();
      ctx.moveTo(100, 100);
      ctx.lineTo(300, 100);
      ctx.stroke();

  }


  return (
    <div className="App">
      <header className="App-header">

        <lable>Alpha</lable>
      <input type="text" value={alpha} onChange={ (e) => {if(e.target.value <= 178){
          setalpha(e.target.value)
      }}}/>
      <lable>Beta</lable>
      <input type="text" value={beta} onChange={ (e) => {if(e.target.value <= 178){
          setbeta(e.target.value)
      }}}/>
      <button type="button" value="calculate" onClick={draw}>calculate </button>

      <p>Alpha = {alpha}</p>
      <p>Beta = {beta}</p>
      <p>Gamma = {gamma}</p>


      <canvas id="canvas" height="400" width="500">
      </canvas>







      </header>
    </div>
  );
}
export default Dreieck;
