import './App.css';
import {useEffect, useState} from "react";
import * as THREE from 'three';

function Dreieck() {


  let geom = new THREE.Geometry();
  let v1 = new THREE.Vector3(0,0,0);
  let v2 = new THREE.Vector3(30,0,0);
  let v3 = new THREE.Vector3(30,30,0);

  geom.vertices.push(v1);
  geom.vertices.push(v2);
  geom.vertices.push(v3);

  geom.faces.push( new THREE.Face3( 0, 1, 2 ) );
  geom.computeFaceNormals();

  let mesh= new THREE.Mesh( geom, new THREE.MeshNormalMaterial() );


    const [alpha, setalpha] = useState(0);
    const [beta, setbeta] = useState(0);
    const [gamma, setgamma] = useState(0);

    function calculategamma(){
      let result;
      result = 180-alpha-beta;
        setgamma(result);
    }


  return (
    <div className="App">
      <header className="App-header">

        <lable>Alpha</lable>
      <input type="text" value={alpha} onChange={ (e) => {if(e.target.value <= 178){
          setalpha(e.target.value)
      }}}/>
      <lable>Beta</lable>
      <input type="text" value={beta} onChange={ (e) => {if(e.target.value <= 178){
          setbeta(e.target.value)
      }}}/>
      <button type="button" value="calculate" onClick={calculategamma}>calculate </button>

      <p>Alpha = {alpha}</p>
      <p>Beta = {beta}</p>
      <p>Gamma = {gamma}</p>






      </header>
    </div>
  );
}
export default Dreieck;
