import {useEffect, useState} from "react";

function Dreieck() {

    const [alphadegree, setalphadegree] = useState(0);
    const [beta, setbeta] = useState(0);
    const [gamma, setgamma] = useState(0);

    function calculategamma(){
      let result;
      result = 180-alphadegree-beta;
        setgamma(result);
    }



  return (
    <div className="App">
      <header className="App-header">

      <lable>Alphadegree Winkel</lable>
      <input type="text" value={alphadegree} onChange={ (e) => {if(e.target.value <= 178){
          setalphadegree(e.target.value)
      }}}/>
      <lable>Beta Winkel</lable>
      <input type="text" value={beta} onChange={ (e) => {if(e.target.value <= 178){
          setbeta(e.target.value)
      }}}/>
      <button type="button" value="calculate" onClick={calculategamma}>calculate </button>

      <p>Alphadegree = {alphadegree}</p>
      <p>Beta = {beta}</p>
      <p>Gamma = {gamma}</p>






      </header>
    </div>
  );
}
export default Dreieck;
