import {useEffect, useState} from "react";

function Dreieck() {

    const [alphaangle, setalphaangle] = useState(0);
    const [betaangle, setbetaangle] = useState(0);
    const [gammaangle, setgammaangle] = useState(0);

    const [alength, setalength] = useState(0);
    const [blength, setblength] = useState(0);

    function calculate(){
      calculateangle();
      calculatelength();

    }

    function calculateangle(){
      let result;
      result = 180-alphaangle-betaangle;
        setgammaangle(result);
    }
    function calculatelength(){
      let result;
      result = Math.sqrt(Math.pow(alength) + Math.pow(betalength))
    }



  return (
    <div className="App">
      <header className="App-header">

      <lable>Alpha Winkel</lable>
      <input type="text" value={alphaangle} onChange={ (e) => {if(e.target.value <= 178){
          setalphaangle(e.target.value)
      }}}/>
      <lable>Beta Winkel</lable>
      <input type="text" value={betaangle} onChange={ (e) => {if(e.target.value <= 178){
          setbetaangle(e.target.value)
      }}}/>
      <lable>A länge </lable>
      <input type="text" value={alength} onChange={ (e) => {if(e.target.value <= 178){
          setalength(e.target.value)
      }}}/>
      <lable>B länge </lable>
      <input type="text" value={blength} onChange={ (e) => {if(e.target.value <= 178){
          setblength(e.target.value)
      }}}/>
      <button type="button" value="calculate" onClick={calculateangle}>calculate </button>

      <p>Alpha° = {alphaangle}</p>
      <p>Beta° = {betaangle}</p>
      <p>Gamma° = {gammaangle}</p>






      </header>
    </div>
  );
}
export default Dreieck;
