"use strict";
var setCtx = require("./setCtx");
/**
 * draw a line
 * 
 * @param {CanvasRenderingContext2D} ctx canvas rendering content
 * @param {Number} sx start point x-coordinate
 * @param {Number} sy start point y-coordinate
 * @param {Number} ex end point x-coordinate
 * @param {Number} ey end point y-coordinate
 * @returns {CanvasRenderingContext2D}
 */
function drawLine(ctx, sx, sy, ex, ey) {
    ctx = setCtx.bind(this)(ctx);

    ctx.beginPath();
    ctx.moveTo(sx, sy);
    ctx.lineTo(ex, ey);
    return !this || this === window ? ctx : this;
}

module.exports = drawLine;
