var _canvas = document.createElement("canvas");
var _ctx = _canvas.getContext("2d");

function setCtx(ctx) {
    if (!_ctx.__proto__.isPrototypeOf(ctx) && ctx !== null) {
        throw TypeError("ctx should be type of CanvasRenderingContext2D or null");
    }

    if (!ctx) {
        if (!this.ctx) throw new ReferenceError("ctx not found!");
        ctx = this.ctx;
    }
    return ctx;
}

module.exports = setCtx;
