"use strict";
var setCtx = require("./setCtx");

/**
 * draw a circle
 * 
 * @param {CanvasRenderingContext2D} ctx canvas rendering content
 * @param {Number} ox origin point x-coordinate
 * @param {Number} oy origin point y-coordinate
 * @param {Number} r radius
 * @returns {CanvasRenderingContext2D}
 */
function drawCircle(ctx, ox, oy, r) {
    ctx = setCtx.bind(this)(ctx);
    
    ctx.beginPath();
    ctx.arc(ox, oy, r, 0, Math.PI * 2, true);
    return !this || this === window ? ctx : this;
}

module.exports = drawCircle;