"use strict";
var circle = require("./circle");
var setCtx = require("./setCtx");

function drawPoint(ctx, pointSize, x, y) {
    ctx = setCtx.bind(this)(ctx);
    circle.call(this, ctx, x, y, pointSize);

    ctx.fillStyle = "white";
    ctx.strokeStyle = "gray";
    ctx.lineWidth = 1;
    ctx.fill()
    ctx.stroke();

    return !this || this === window ? ctx : this;
}

module.exports = drawPoint;