"use strict";

function currying() {
    var array = [].slice.call(arguments);
    var func = array.shift();

    if (typeof func !== "function") {
        throw TypeError("first argument must be type of function");
    }

    return function () {
        var args = [].slice.call(arguments);
        args = array.concat(args);
        return func.apply(this, args);
    }
}

module.exports = currying;